<template>
  <header class="header">
    <div class="layout">
      <img class="logo" src="data:img/png;base64,">
      <span class="avatar__right" @click="toggle">
        <v-img-circle :diameter="40"/>
        <span class="angle" :class="{ 'icon-circle-up': !open, 'icon-circle-down': open }"/>
      </span>
      <ul class="profile text-left" :class="{ open: open }" @blur="toggle">
        <router-link tag="li" to="#4" @click.native="toggle">
          <p>
            <span class="icon-profile"/>profile
          </p>
        </router-link>
        <router-link tag="li" to="#5" @click.native="toggle">
          <p>
            <span class="icon-exit"/>Sign Out
          </p>
        </router-link>
      </ul>
    </div>
  </header>
</template>

<script>
export default {
  name: 'lhead',
  data () {
    return {
      open: false
    }
  },
  methods: {
    toggle () {
      this.open = !this.open
    }
  },
  components: {
    'v-img-circle': () => import('@/components/img/circle')
  },
  mounted () {
    document.body.addEventListener('click', (e) => {
      if (!document.getElementsByClassName('avatar__right')[0].contains(e.target)) this.open = false
    })
  }
}
</script>

<!-- Add "scoped" attribute to limit CSS to this component only -->
<style type="text/css" lang="scss" scoped>
   @import 'index.scss';
</style>
